<?phpnamespace Nemundo\Pdf\Geometry;use Nemundo\Pdf\Base\AbstractPdfObject;use Nemundo\Pdf\Color\PdfColor;use Nemundo\Pdf\Document\AbstractPdfDocument;class PdfRectangle extends AbstractPdfObject{    /**     * @var int     */    public $x;    /**     * @var int     */    public $y;    /**     * @var int     */    public $width;    /**     * @var int     */    public $height;    /**     * @var PdfColor     */    public $color;    /**     * @var int     */    public $borderWidth;    /**     * @var Color     */    public $borderColor;    public function __construct(AbstractPdfDocument $pdfDocument)    {        parent::__construct($pdfDocument);        $this->color = new PdfColor();    }    public function renderPdf(\FPDF $fpdf)    {        $fpdf->SetFillColor($this->color->r, $this->color->g, $this->color->b);        $fpdf->Rect($this->x, $this->y, $this->width, $this->height, 'F');    }}